public class AreaTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//변수 선언
		final double PI=3.141592;
		double radius = 3.0;
		//원의 면적 계산
		double area= radius* radius*PI;
		
		//결과 출력		
		System.out.println("반지름이 " +radius+"인 원의 면적은 "+area+"입니다");
	
	}

}
