public class helloloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Hello World 출력
		
		
		int i=10;
		
		
		do {
		//String s1="Hello World!";
			System.out.println( "i의 값: "+i);
			i++;
		}while(i<3);
		
		
		for(int d=0;d<5;d++)
		{
			System.out.println("d의 값은: "+d);
			
		}
		
}
}